    private void tipo_fondoItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_tipo_fondoItemStateChanged
if(tipo_fondo.getSelectedItem().toString().equals("Imagen")){
mostrar_e("aun no");
}   
else if(tipo_fondo.getSelectedItem().toString().equals("Color")){
Color color=JColorChooser.showDialog(null, "Seleccione Color", Color.white);
jPanel1.setBackground(color);
}// TODO add your handling code here:
    }//GEN-LAST:event_tipo_fondoItemStateChanged
public void mostrar_e(String e){
    most_e.setAlignmentY(CENTER_ALIGNMENT);
            most_e.setHorizontalAlignment(WIDTH / 2);
            most_e.setAlignmentX(CENTER_ALIGNMENT);
            most_e.setText(e);
            most_e.setForeground(Color.red);
           
}
